import React, { useEffect } from 'react'
import { useForm } from 'react-hook-form'
import emailjs from 'emailjs-com'


const ContactForm = () => {
  const { handleSubmit, register, setValue } = useForm()
  useEffect(() => {
    register(
      { name: 'name' }
    )
    register(
        { name: 'email' }
      )
    register({name: 'phone_number'})
    register({name: 'msg_subject'})

    register({name: 'message'})

  }, [])

  const onsubmit = (values)=>{

    const { name, email, phone_number, msg_subject, message} = values
    console.log('v ',values)

    // emailjs.send('Gmail','template_rng2cws', {
    //     name,
    //     email,
    //     phone_number,
    //     msg_subject,
    //     message,
    // },'user_ojITwwnFvXK7d1iaDiJ2U')
    // .then(function(response) {
    //    console.log('SUCCESS!', response.status, response.text);
    // }, function(err) {
    //    console.log('FAILED...', err);
    // });



  }

  return (
    <div className='contact-form'>
      <h3>Let's make your app !</h3>

      <form id='contactForm'>
        <div className='row'>
          <div className='col-lg-6 col-md-6'>
            <div className='form-group'>
              <input
                type='text'
                name='name'
                id='name'
                className='form-control'
                placeholder='Your Name'
                required
                onChange={event => setValue('name', event.target.value, true)}
              />
            </div>
          </div>

          <div className='col-lg-6 col-md-6'>
            <div className='form-group'>
              <input
                type='email'
                name='email'
                id='email'
                className='form-control'
                placeholder='Your Email'
                required
                onChange={event => setValue('email', event.target.value, true)}
              />
            </div>
          </div>

          <div className='col-lg-6 col-md-6'>
            <div className='form-group'>
              <input
                type='text'
                name='phone_number'
                id='phone_number'
                className='form-control'
                placeholder='Your Phone'
                required
                onChange={event => setValue('phone_number', event.target.value, true)}
              />
            </div>
          </div>

          <div className='col-lg-6 col-md-6'>
            <div className='form-group'>
              <input
                type='text'
                name='msg_subject'
                id='msg_subject'
                className='form-control'
                placeholder='Your Subject'
                required
                onChange={event => setValue('msg_subject', event.target.value, true)}
              />
            </div>
          </div>

          <div className='col-lg-12 col-md-12'>
            <div className='form-group'>
              <textarea
                onChange={event => setValue('message', event.target.value, true)}
                name='message'
                className='form-control'
                id='message'
                rows='6'
                placeholder='Your Message'></textarea>
            </div>
          </div>

          <div className='col-lg-12 col-md-12 text-center'>
            <button onClick={handleSubmit(onsubmit)} type='submit' className='default-btn'>
              <i className='bx bxs-paper-plane'></i>
              Send Message
              <span></span>
            </button>
          </div>
        </div>
      </form>
    </div>
  )
}

export default ContactForm
