import React, { useEffect, useState } from 'react'
import { useForm } from 'react-hook-form'
import emailjs from 'emailjs-com'
import { CircularProgress } from '@material-ui/core'

const ContactForm = () => {
  const { handleSubmit, register, setValue } = useForm()
  const [loading, setloading] = useState()
//   useEffect(() => {
//     register({ name: 'name' })
//     register({ name: 'email' })
//     register({ name: 'phone_number' })
//     register({ name: 'msg_subject' })
//     register({ name: 'message' })
//   }, [])

  const onsubmit = values => {
    const { name, email, phone_number, msg_subject, message } = values
    console.log('v ', values)
    setloading(true)
    // emailjs
    //   .send(
    //     'Gmail',
    //     'template_rng2cws',
    //     {
    //       name,
    //       email,
    //       phone_number,
    //       msg_subject,
    //       message,
    //     },
    //     'user_ojITwwnFvXK7d1iaDiJ2U',
    //   )
    //   .then(
    //     function (response) {
    //       setloading(false)
    //       console.log('SUCCESS!', response.status, response.text)
    //     },
    //     function (err) {
    //       setloading(false)
    //       console.log('FAILED...', err)
    //     },
    //   )
  }

  return (
    <div className='contact-form'>
      <h3>Let's make your app !</h3>

      <form onSubmit={handleSubmit(onsubmit)} id='contactForm'>
        <div className='row'>
          <div className='col-lg-6 col-md-6'>
            <div className='form-group'>
              <input
                type='text'
                name='name'
                id='name'
                className='form-control'
                placeholder='Your Name'
                ref={register}
                required
                onChange={event => setValue('name', event.target.value, true)}
              />
            </div>
          </div>

          <div className='col-lg-6 col-md-6'>
            <div className='form-group'>
              <input
                type='email'
                name='email'
                id='email'
                ref={register}
                className='form-control'
                placeholder='Your Email'
                required
                onChange={event => {
                  setValue('email', event.target.value, true)
                }}
              />
            </div>
          </div>

          <div className='col-lg-6 col-md-6'>
            <div className='form-group'>
              <input
                type='text'
                name='phone_number'
                id='phone_number'
                ref={register}
                className='form-control'
                placeholder='Your Phone'
                required
                onChange={event => {
                  setValue('phone_number', event.target.value, true)
                }}
              />
            </div>
          </div>

          <div className='col-lg-6 col-md-6'>
            <div className='form-group'>
              <input
                type='text'
                name='msg_subject'
                id='msg_subject'
                className='form-control'
                placeholder='Your Subject'
                ref={register}
                required
                onChange={event => setValue('msg_subject', event.target.value, true)}
              />
            </div>
          </div>

          <div className='col-lg-12 col-md-12'>
            <div className='form-group'>
              <textarea
                onChange={event => setValue('message', event.target.value, true)}
                name='message'
                className='form-control'
                id='message'
                rows='6'
                ref={register}
                placeholder='Your Message'></textarea>
            </div>
          </div>

          <div className='col-lg-12 col-md-12 text-center'>
            {loading ? (
              <CircularProgress color='secondary' />
            ) : (
              <button type='submit' className='default-btn'>
                <i className='bx bxs-paper-plane'></i>
                Send Message
                <span></span>
              </button>
            )}
          </div>
        </div>
      </form>
    </div>
  )
}

export default ContactForm
